import React, { useEffect, useState } from 'react';

const ListTodo = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/todo')
          .then((res) => res.json())
          .then((data) => {
            console.log('Fetched todos:', data);
            setTodos(data);
          })
          .catch((err) => console.error('Fetch error:', err));
      }, []);
      

    const handleEdit = (id) => {
        // Handle edit logic here
    };

    const handleDelete = (id) => {
        // Handle delete logic here
    };
    console.log(todos);


    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <table style={{ borderCollapse: 'collapse', width: '400px', textAlign: 'center' }}>
                <thead>
                    <tr>
                        <th style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>Description</th>
                        <th style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>Edit</th>
                        <th style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {todos.map(todo => (
                        <tr key={todo.id}>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>{todo.description}</td>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>
                                <button style={{ backgroundColor: 'blue', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '4px' }} onClick={() => handleEdit(todo.id)}>Edit</button>
                            </td>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>
                                <button style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '4px' }} onClick={() => handleDelete(todo.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                        <tr key={todo.id}>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>{todo.description}</td>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>
                                <button onClick={() => handleEdit(todo.id)}>Edit</button>
                            </td>
                            <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>
                                <button onClick={() => handleDelete(todo.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    ); 
}
export default ListTodo;
