import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditTodo.css'; // You can style it as you want

const EditTodo = () => {
  const { id } = useParams();
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false); // For loading state
  const [error, setError] = useState(null); // For error handling
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/todo/${id}`)
      .then((res) => setDescription(res.data.description))
      .catch((err) => console.error('Failed to fetch todo:', err));
  }, [id]);

  const handleUpdate = () => {
    if (!description.trim()) return;

    setLoading(true); // Set loading to true when updating
    setError(null); // Reset error message

    axios
      .put(`http://localhost:5000/todo/${id}`, { description })
      .then(() => {
        setLoading(false); // Set loading to false after update
        navigate('/'); // Navigate to home page after update
      })
      .catch((err) => {
        setLoading(false); // Set loading to false if error occurs
        setError('Failed to update todo. Please try again.');
        console.error('Failed to update todo:', err);
      });
  };

  return (
    <div className="edit-container">
      <h2>Edit Todo</h2>
      <textarea
        className="edit-textbox"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        rows="4"
      />
      <button className="edit-button" onClick={handleUpdate} disabled={loading}>
        {loading ? 'Updating...' : 'Update'}
      </button>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default EditTodo;
