import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
// import './EditTodo.css'; // Add styles if needed

const EditTodo = () => {
  const { id } = useParams(); // Get the todo id from the URL
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the Todo to edit by ID
    axios
      .get(`http://localhost:5000/todo/${id}`)
      .then((res) => {
        setDescription(res.data.description);
      })
      .catch((err) => {
        console.error('Error fetching Todo for editing:', err);
      });
  }, [id]);

  const handleSave = () => {
    // Update the Todo with the new description
    axios
      .put(`http://localhost:5000/todo/${id}`, { description })
      .then((res) => {
        console.log('Todo updated:', res.data);
        // Navigate back to the list after saving
        navigate('/');
      })
      .catch((err) => {
        console.error('Error updating Todo:', err);
      });
  };

  return (
    <div className="edit-todo-container">
      <h2>Edit Todo</h2>
      <div>
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
      </div>
      <button className="save-button" onClick={handleSave}>
        Save
      </button>
    </div>
  );
};

export default EditTodo;
