import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditTodo = () => {
  const { id } = useParams(); // Get the todo ID from the URL
  const navigate = useNavigate();
  const [todo, setTodo] = useState({ title: '', completed: false });

  // Fetch the todo item when the component mounts
  useEffect(() => {
    const fetchTodo = async () => {
      try {
        const response = await axios.get(`/api/todos/${id}`);
        setTodo(response.data);
      } catch (error) {
        console.error('Error fetching todo:', error);
      }
    };

    fetchTodo();
  }, [id]);

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTodo((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle checkbox change
  const handleCheckboxChange = (e) => {
    setTodo((prevState) => ({
      ...prevState,
      completed: e.target.checked,
    }));
  };

  // Handle form submission to update the todo
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/todos/${id}`, todo); // Update the todo
      navigate('/'); // Redirect to the main page after updating
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  return (
    <div>
      <h2>Edit Todo</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={todo.title}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="completed">Completed</label>
          <input
            type="checkbox"
            id="completed"
            name="completed"
            checked={todo.completed}
            onChange={handleCheckboxChange}
          />
        </div>
        <button type="submit">Update Todo</button>
      </form>
    </div>
  );
};

export default EditTodo;
