import React, { useEffect, useState } from 'react';

const ListTodo = () => {
  console.log('üöÄ ListTodo component mounted');

  const [todos, setTodos] = useState([]);

  useEffect(() => {
    console.log('üì° useEffect running - fetching todos...');
    fetch('http://localhost:5000/todo')
      .then((response) => {
        console.log('‚úÖ Got response:', response);
        return response.json();
      })
      .then((data) => {
        console.log('üì¶ Parsed JSON:', data);
        setTodos(data);
      })
      .catch((error) => {
        console.error('‚ùå Fetch error:', error);
      });
  }, []);

  return (
    <div style={{ marginTop: '2rem' }}>
      <h2>Todo List</h2>
      {todos.length === 0 ? (
        <p>‚è≥ Loading or no todos found...</p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {todos.map((todo) => (
              <tr key={todo.id}>
                <td>{todo.description}</td>
                <td>
                  <button>Edit</button>
                </td>
                <td>
                  <button>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ListTodo;
