import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ListTodo.css'; 

const ListTodo = () => {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/todo')
      .then((res) => res.json())
      .then((data) => {
        console.log('Fetched todos:', data);
        setTodos(data);
      })
      .catch((err) => console.error('Fetch error:', err));
  }, []);

  const handleEdit = (id) => {
    console.log('Edit clicked for ID:', id);
    // Navigate to the EditTodo component, passing the id
    navigate(`/todo/${id}`);
  };

  const handleDelete = (id) => {
    console.log('Delete clicked for ID:', id);
    // Implement your delete logic here (e.g., send a DELETE request to your API)
  };

  return (
    <div className="todo-container">
      <table className="todo-table">
        <thead>
          <tr className="todo-header-row">
            <th className="todo-cell">Description</th>
            <th className="todo-cell">Edit</th>
            <th className="todo-cell">Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td className="todo-cell">{todo.description}</td>
              <td className="todo-cell">
                 {/* Edit button that links to /todo/:id */}
                 <Link to={`/todo/${todo.id}`}>
                  <button className="todo-button">Edit</button>
                </Link>
              </td>
              <td className="todo-cell">
                <button className="todo-button" onClick={() => handleDelete(todo.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="todo-bottom-space"></div>
    </div>
  );
};

export default ListTodo;
