import React, { useEffect, useState } from 'react';

const ListTodo = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/todo')
      .then((res) => res.json())
      .then((data) => {
        console.log('Fetched todos:', data);
        setTodos(data);
      })
      .catch((err) => console.error('Fetch error:', err));
  }, []);

  const handleEdit = (id) => {
    console.log('Edit clicked for ID:', id);
  };

  const handleDelete = (id) => {
    console.log('Delete clicked for ID:', id);
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        paddingTop: '2rem',
        backgroundColor: '#f9f9f9',
        minHeight: '100vh',
      }}
    >
      <table
        style={{
          borderCollapse: 'collapse',
          width: '600px',
          textAlign: 'center',
          backgroundColor: 'white',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',
          borderRadius: '8px',
          overflow: 'hidden',
          minHeight: '100mv',
        }}
      >
        <thead>
          <tr style={{ backgroundColor: '#f2f2f2' }}>
            <th style={{ padding: '14px', border: '1px solid #000' }}>Description</th>
            <th style={{ padding: '14px', border: '1px solid #000' }}>Edit</th>
            <th style={{ padding: '14px', border: '1px solid #000' }}>Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td style={{ padding: '12px', border: '1px solid #000' }}>
                {todo.description}
              </td>
              <td style={{ padding: '12px', border: '1px solid #000' }}>
                <button
                  style={{
                    padding: '6px 12px',
                    borderRadius: '6px',
                    border: '1px solid black',
                    backgroundColor: '#CDC8C8',
                    color: '#333',
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                  }}
                  onClick={() => handleEdit(todo.id)}
                >
                  Edit
                </button>
              </td>
              <td style={{ padding: '12px', border: '1px solid #000' }}>
                <button
                  style={{
                    padding: '6px 12px',
                    borderRadius: '6px',
                    border: '1px solid black',
                    backgroundColor: '#CDC8C8',
                    color: '#333',
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                  }}
                  onClick={() => handleDelete(todo.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListTodo;
